mapper.update:
update `systemlog`
set opUser_id = #{opuser.id},
  opTime = #{optime},
  oplp = #{oplp},
  function = #{function},
  params = #{params}
where id = #{id}

mapper.select:
select s.id, s.opTime, s.oplp, s.function, s.params, e.id e_id, e.username e_username
from `systemlog` s LEFT JOIN employee e ON  opUser_id = e.id
where id = #{id}
